import java.util.*;
import java.lang.*;

class Solution {
   public List<String> allPrefixesAndSubstrings(String string) throws IllegalArgumentException {
        if (string == null || string.length() == 0){
            throw new IllegalArgumentException("Input cannot be null or empty");
        }

        List<String> result = new ArrayList<>();
        String[] words = string.split("\\s+");
        StringBuilder prefix = new StringBuilder();
        for (String word : words) {
            prefix.append(word).append(" ");
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < word.length(); i++) {
                sb.append(word.charAt(i));
                result.add(sb.toString());
            }
            result.add(prefix.toString().trim());
        }

        result.sort((s1, s2) -> s1.length() - s2.length());

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.allPrefixesAndSubstrings("abc def"));
    }
}

