class Solution {
    public List<Integer> strangeSortList(List<Integer> lst) {
        if (lst.isEmpty()) {
            return lst;
        }

        Collections.sort(lst);
        List<Integer> result = new ArrayList<>();
        while (!lst.isEmpty()) {
            result.add(lst.remove(0)); // Add smallest
            if (!lst.isEmpty()) {
                result.add(lst.remove(lst.size() - 1)); // Add largest
            }
        }
        return result;
    }

