class Triangle:
    count = 0

    def __init__(self, side1, side2, side3):
        self.side1 = side1
        self.side2 = side2
        self.side3 = side3
        Triangle.count += 1

    def get_area(self):
        def calculate_area(side1, side2, side3):
            if side1 <= 0 or side2 <= 0 or side3 <= 0:
                return 0
            elif side1 >= side2 + side3 or side2 >= side1 + side3 or side3 >= side1 + side2:
                return 0
            else:
                s = (side1 + side2 + side3) / 2
                area = (s * (s - side1) * (s - side2) * (s - side3)) ** 0.5
                return area

        return calculate_area(self.side1, self.side2, self.side3)

    def get_perimeter(self):
        return self.side1 + self.side2 + self.side3

    @property
    def is_equilateral(self):
        return self.side1 == self.side2 and self.side2 == self.side3

    def is_isosceles(self):
        return self.side1 == self.side2 or self.side2 == self.side3 or self.side3 == self.side1

    def resize(self, new_side1, new_side2, new_side3):
        class ResizeError(Exception):
            pass

        if new_side1 <= 0 or new_side2 <= 0 or new_side3 <= 0:
            raise ResizeError("All sides must be positive integers.")
        elif new_side1 >= new_side2 + new_side3 or new_side2 >= new_side1 + new_side3 or new_side3 >= new_side1 + new_side2:
